mapper.select:
select  *  from  admin_info
where
    email=#{email} and userpass=#{password}

select  *  from  admin_info
where
    phone=#{param1} and userpass=#{param2}

select a.id,a.username,a.nickname,a.sex,a.phone,a.email,r.rolename,r.roleremark from admin_info a left join role_info r on a.role_id = r.role_id
a.username like concat('%',#{username},'%')
limit #{page},#{limit}
