mapper.select:
select count(Id) from temt7.teacher_info


        username like concat('%',#{username},'%')


        and collage=#{collage}

SELECT Id, username, sex,birthday,grade,rank,jobDate,collage,password,phone,roleId FROM temt7.teacher_info


        username LIKE CONCAT('%', #{username}, '%')


        AND sex = #{sex}


        AND birthday = #{birthday}


        AND grade = #{grade}


        AND rank = #{rank}


        AND jobDate = #{jobDate}


        AND collage = #{collage}


        AND password = #{password}


        AND phone = #{phone}


        AND roleId = #{roleId}


limit #{index},#{limit}

mapper.update:
UPDATE temt7.teacher_info
SET username = #{username}, sex = #{sex},birthday = #{birthday},
    grade = #{grade}, rank = #{rank},jobDate = #{jobDate},
    collage = #{collage},
    password = #{password}, phone = #{phone},roleId = #{roleId}
WHERE Id = #{Id}
